#!/bin/sh

die () {
   echo "*** mkconfig: $*" 1>&2
   exit 1
}

[ -f makefile ] || die "can not find 'makefile'"

cat<<EOF
/**************************************************************/
/* Automatically generated by './mkconfig.sh'.  Do note edit! */
/**************************************************************/
EOF

echo "#define YALS_OS \"`uname -srmn`\""
echo "#define YALS_COMPILED \"`date`\""
echo "#define YALS_RELEASED \"Wed Apr 20 16:11:04 CEST 2016\""
echo "#define YALS_VERSION \"03r\""
echo "#define YALS_ID \"b8cf0fb483228c4217ba26d5864ab0a8848bf8d5\""
